import React from "react";
import "./App.css";
import { useSelector,useDispatch } from "react-redux";
const App = () => {
const myState =useSelector((state)=>state.changeTheNumber);
  return (
    <>
      <div classname="App-header ">
        <h1>Increment/Decrement counter</h1>
        <h4>using react and redux</h4>

        <div classname="App">
          <button classname="App" title="Decrement">
            <span> - </span>
          </button>
          <input name="quantity" type="text" classname="App" value="0" />
          <button classname="App" title="Increment">
            {" "}
            <span>+</span>
          </button>
        </div>
      </div>
    </>
  );
};

export default App;
